// ‚úÖ src/pages/VerLecturaPage.jsx
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const imagenFallback = "https://upload.wikimedia.org/wikipedia/commons/thumb/a/ac/No_image_available.svg/480px-No_image_available.svg.png";

const VerLecturaPage = () => {
  const { id } = useParams();
  const [lectura, setLectura] = useState(null);

  useEffect(() => {
    const data = localStorage.getItem('lectura_seleccionada');
    if (data) {
      const lecturaObj = JSON.parse(data);
      if (parseInt(lecturaObj.id) === parseInt(id)) {
        setLectura(lecturaObj);
      }
    }
  }, [id]);

  if (!lectura) {
    return <p className="text-warning p-3">‚ö†Ô∏è No se encontr√≥ la lectura seleccionada.</p>;
  }

  return (
    <div className="container py-4">
      <h2 className="mb-4">üìñ Detalle de la Lectura</h2>

      <div className="card p-3">
        {lectura.imagen_url && (
          <div className="text-center">
            <img
              src={lectura.imagen_url || imagenFallback}
              alt="Portada"
              style={{ width: '150px', height: '220px', objectFit: 'cover' }}
              onError={(e) => { e.target.src = imagenFallback; }}
            />
          </div>
        )}

        <h3 className="mt-3">{lectura.titulo}</h3>
        <p><strong>Autor:</strong> {lectura.autor}</p>
        <p><strong>Puntaje:</strong> {lectura.puntaje ?? 'N/A'}</p>
        <p><strong>Comentario:</strong> {lectura.comentario || 'Sin comentarios'}</p>
        <p><strong>Inicio:</strong> {lectura.fecha_inicio || 'No registrado'}</p>
        <p><strong>Fin:</strong> {lectura.fecha_fin || 'No registrado'}</p>
        <p><strong>Lugar:</strong> {lectura.lugar_fin || 'No especificado'}</p>
      </div>
    </div>
  );
};

export default VerLecturaPage;
