// Importamos React y los hooks necesarios
import React, { useEffect, useState } from 'react';
import axios from 'axios';

function MisLibros() {
  // Estado para guardar los libros obtenidos del backend
  const [libros, setLibros] = useState([]);

  // useEffect para cargar los libros al montar el componente
  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/libros/') // Llama a la API
      .then((res) => setLibros(res.data)) // Guarda los datos recibidos
      .catch((err) => console.error('Error al cargar libros', err)); // Maneja errores
  }, []); // Se ejecuta solo una vez al montar

  return (
    <div className="container">
      <h2>Mis Libros Guardados</h2>

      {/* Lista de libros obtenidos */}
      <ul className="list-group">
        {libros.map((libro) => (
          <li key={libro.id} className="list-group-item d-flex justify-content-between align-items-center">
            <div>
              <strong>{libro.titulo}</strong> - {libro.autor}
            </div>
            {libro.imagen_url && (
              <img src={libro.imagen_url} alt="Portada" height="80" className="ms-2" />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MisLibros;
