// src/pages/Home.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import ButtonHome from '../components/ui/ButtonHome'; // importar componente

const Home = () => {
  const navigate = useNavigate();
  const goTo = (path) => navigate(path);

  return (
    <div className="min-h-screen bg-black text-white flex flex-col items-center justify-between py-10 px-4">
      <header className="w-full flex justify-between items-center border-b border-white pb-4">
        <h1 className="text-2xl font-bold tracking-wide">📚 ReadMe</h1>
        <nav className="space-x-6">
          <button onClick={() => goTo('/cargar')} className="hover:underline">Cargar Lectura</button>
          <button onClick={() => goTo('/mis-libros')} className="hover:underline">Mis Libros</button>
          <button onClick={() => goTo('/mis-lecturas')} className="hover:underline">Mis Lecturas</button>
        </nav>
      </header>

      <main className="flex flex-col items-center gap-8 mt-20">
        <p className="text-lg mb-4">Hola, Berenice. ¿Qué querés hacer hoy?</p>

        <ButtonHome icon="🔍" text="Buscar" onClick={() => goTo('/buscar')} />
        <ButtonHome icon="✏️" text="Cargar Lectura" onClick={() => goTo('/cargar')} />
        <ButtonHome icon="🌎" text="Explorar" onClick={() => goTo('/explorar')} />
      </main>


    </div>
  );
};

export default Home;
