import React, { useEffect, useState } from 'react';
import { obtenerLecturas } from '../../api/libros';

const MisLecturas = () => {
  const [lecturas, setLecturas] = useState([]);

  useEffect(() => {
    const fetchLecturas = async () => {
      try {
        const data = await obtenerLecturas();
        setLecturas(data);
      } catch (error) {
        console.error('Error al cargar lecturas', error);
      }
    };

    fetchLecturas();
  }, []);

  return (
    <div className="container mt-4">
      <h2>ðŸ“– Mis Lecturas</h2>
      {lecturas.length === 0 ? (
        <p>No hay lecturas registradas aÃºn.</p>
      ) : (
        <ul>
          {lecturas.map((libro) => (
            <li key={libro.id}>
              <h4>{libro.titulo}</h4>
              <p>Autor: {libro.autor}</p>
              <p>Fecha de lectura: {libro.fecha_fin || 'No especificada'}</p>
              {libro.imagen_url && <img src={libro.imagen_url} alt={libro.titulo} style={{ width: '100px' }} />}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MisLecturas;
