import React, { useState } from 'react';
import axios from 'axios';

function RegistrarLectura({ libroId, onLecturaGuardada }) {
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [lugarFin, setLugarFin] = useState('');
  const [puntaje, setPuntaje] = useState('');
  const [comentario, setComentario] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      usuario_id: 1, // usuario hardcodeado
      libro_id: libroId,
      fecha_inicio: fechaInicio,
      fecha_fin: fechaFin,
      lugar_fin: lugarFin,
      puntaje: puntaje || null,
      comentario: comentario || '',
    };

    try {
      await axios.post('http://127.0.0.1:8000/api/registrar-lectura/', payload);
      alert('ðŸ“˜ Lectura registrada correctamente');
      onLecturaGuardada();  // Notifica al padre si lo necesita
    } catch (error) {
      console.error('Error al registrar lectura:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-3 border rounded mt-3">
      <h4>Registrar Lectura</h4>

      <div className="mb-2">
        <label>Fecha de inicio</label>
        <input
          type="date"
          className="form-control"
          value={fechaInicio}
          onChange={(e) => setFechaInicio(e.target.value)}
        />
      </div>

      <div className="mb-2">
        <label>Fecha de fin</label>
        <input
          type="date"
          className="form-control"
          value={fechaFin}
          onChange={(e) => setFechaFin(e.target.value)}
        />
      </div>

      <div className="mb-2">
        <label>Lugar donde lo terminaste</label>
        <input
          type="text"
          className="form-control"
          value={lugarFin}
          onChange={(e) => setLugarFin(e.target.value)}
        />
      </div>

      <div className="mb-2">
        <label>Puntaje (1-10)</label>
        <input
          type="number"
          className="form-control"
          value={puntaje}
          onChange={(e) => setPuntaje(e.target.value)}
          min="1"
          max="10"
        />
      </div>

      <div className="mb-2">
        <label>Comentario</label>
        <textarea
          className="form-control"
          value={comentario}
          onChange={(e) => setComentario(e.target.value)}
          rows="3"
        />
      </div>

      <button className="btn btn-success" type="submit">Guardar lectura</button>
    </form>
  );
}

export default RegistrarLectura;
