// src/pages/Home.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();

  const goTo = (path) => navigate(path);

  return (
    <div className="min-h-screen bg-black text-white flex flex-col items-center justify-between py-10 px-4">
      <header className="w-full flex justify-between items-center border-b border-white pb-4">
        <h1 className="text-2xl font-bold tracking-wide">üìö ReadMe</h1>
        <nav className="space-x-6">
          <button onClick={() => goTo('/cargar')} className="hover:underline">Cargar Lectura</button>
          <button onClick={() => goTo('/mis-libros')} className="hover:underline">Mis Libros</button>
          <button onClick={() => goTo('/mis-lecturas')} className="hover:underline">Mis Lecturas</button>
        </nav>
      </header>

      <main className="flex flex-col items-center gap-8 mt-20">
        <p className="text-lg mb-4">Hola, Berenice. ¬øQu√© quer√©s hacer hoy?</p>

        <button
          onClick={() => goTo('/buscar')}
          className="bg-white text-black text-xl px-12 py-6 rounded-2xl hover:scale-105 transition-transform shadow-lg"
        >
          üîç Buscar
        </button>

        <button
          onClick={() => goTo('/cargar')}
          className="bg-white text-black text-xl px-12 py-6 rounded-2xl hover:scale-105 transition-transform shadow-lg"
        >
          ‚úèÔ∏è Cargar Lectura
        </button>

        <button
          onClick={() => goTo('/explorar')}
          className="bg-white text-black text-xl px-12 py-6 rounded-2xl hover:scale-105 transition-transform shadow-lg"
        >
          üåé Explorar
        </button>
      </main>

      <footer className="mt-auto pt-10 text-sm text-gray-400">
        üìö Todos los derechos reservados - ReadMe App
      </footer>
    </div>
  );
};

export default Home;
