import React, { useState } from 'react';
import axios from 'axios';

function BuscarLibro() {
  const [titulo, setTitulo] = useState('');
  const [resultados, setResultados] = useState([]);

  // Buscar libros por t√≠tulo usando la API de Django
  const buscar = async () => {
    try {
      const res = await axios.get(`http://127.0.0.1:8000/api/buscar-libros/${titulo}/`);
      setResultados(res.data);
    } catch (err) {
      console.error('Error al buscar libros', err);
    }
  };

  // Guardar un libro llamando a la API de Django
  const guardarLibro = async (isbn) => {
    const idUsuario = 1; // Se puede hacer din√°mico si ten√©s login
    try {
      await axios.get(`http://127.0.0.1:8000/api/guardar-libro/${isbn}/${idUsuario}/`);
      alert('üìò Libro guardado correctamente');
    } catch (err) {
      alert('‚ùå Error al guardar el libro');
    }
  };

  return (
    <div className="container mt-4">
      <h2>Buscar libro</h2>

      {/* Campo de b√∫squeda y bot√≥n */}
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
          placeholder="T√≠tulo del libro"
        />
        <button className="btn btn-primary" onClick={buscar}>
          Buscar
        </button>
      </div>

      {/* Si no hay resultados */}
      {resultados.length === 0 && (
        <p className="text-muted">üîç No hay resultados para mostrar</p>
      )}

      {/* Lista de libros */}
{resultados.map((libro, index) => {
  // Mostrar en consola la URL de la imagen y el ISBN
  console.log("ISBN:", libro.isbn);
  console.log("URL de imagen:", libro.isbn && libro.isbn.trim() !== ""
    ? `https://covers.openlibrary.org/b/isbn/${libro.isbn}-L.jpg`
    : 'https://via.placeholder.com/100x150?text=Sin+Portada' );

  return (
    <div key={index} className="card mb-3">
            <div className="row g-0">
              {/* Imagen del libro */}
              <div className="col-md-2 d-flex align-items-center justify-content-center p-2">
<img
  src={
    libro.isbn && libro.isbn.trim() !== ""
      ? `https://covers.openlibrary.org/b/isbn/${libro.isbn}-L.jpg`
      : 'https://via.placeholder.com/100x150?text=Sin+Portada'
  }
  alt="Portada del libro"
  onError={(e) => {
    e.target.onerror = null;
    e.target.src = 'https://via.placeholder.com/100x150?text=Sin+Portada';
  }}
  style={{ width: '100px', height: '150px', objectFit: 'cover' }}
/>


              </div>

              {/* Informaci√≥n del libro */}
              <div className="col-md-10">
                <div className="card-body">
                  <h5 className="card-title">{libro.titulo}</h5>
                  <p className="card-text">{libro.autor} ({libro.anio})</p>
                  <button
                    className="btn btn-primary"
                    onClick={() => guardarLibro(libro.isbn)}
                  >
                    Guardar
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default BuscarLibro;
