import React, { useState } from 'react';
import axios from 'axios';

function BuscarLibro() {
  const [titulo, setTitulo] = useState('');
  const [resultados, setResultados] = useState([]);

  // Imagen por defecto si no hay portada
  const imagenFallback = "https://upload.wikimedia.org/wikipedia/commons/thumb/a/ac/No_image_available.svg/480px-No_image_available.svg.png";

  // Buscar libros por t√≠tulo usando la API de Django
  const buscar = async () => {
    try {
      const res = await axios.get(`http://127.0.0.1:8000/api/buscar-libros/${titulo}/`);
      setResultados(res.data);
      console.log("Resultados recibidos:", res.data); // ‚úÖ solo 1 vez
    } catch (err) {
      console.error('Error al buscar libros', err);
    }
  };

  // Guardar libro
  const guardarLibro = async (isbn) => {
    const idUsuario = 1;
    try {
      await axios.get(`http://127.0.0.1:8000/api/guardar-libro/${isbn}/${idUsuario}/`);
      alert('üìò Libro guardado correctamente');
    } catch (err) {
      alert('‚ùå Error al guardar el libro');
    }
  };

  return (
    <div className="container mt-4">
      <h2>Buscar libro</h2>

      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
          placeholder="T√≠tulo del libro"
        />
        <button className="btn btn-primary" onClick={buscar}>
          Buscar
        </button>
      </div>

      {resultados.length === 0 && (
        <p className="text-muted">üîç No hay resultados para mostrar</p>
      )}

      {resultados.map((libro, index) => {
        console.log("ISBN:", libro.isbn);
        console.log("URL de imagen:", libro.portada || imagenFallback);

        return (
          <div key={index} className="card mb-3">
            <div className="row g-0">
              <div className="col-md-2 d-flex align-items-center justify-content-center p-2">
                <img
                  src={libro.portada || imagenFallback}
                  alt="Portada"
                  onError={(e) => { e.target.src = imagenFallback; }}
                  style={{ width: '100px', height: '150px', objectFit: 'cover' }}
                />
              </div>

              <div className="col-md-10">
                <div className="card-body">
                  <h5 className="card-title">{libro.titulo}</h5>
                  <p className="card-text">{libro.autor} ({libro.anio})</p>
                  <button
                    className="btn btn-primary"
                    onClick={() => guardarLibro(libro.isbn)}
                  >
                    Guardar
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default BuscarLibro;
