import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = () => {
  const location = useLocation();

  return (
    <nav style={styles.navbar}>
      <Link to="/" style={styles.logo}>
        ðŸ“š README
      </Link>
      <div style={styles.links}>
        <Link
          to="/"
          style={{ ...styles.link, ...(location.pathname === '/' ? styles.active : {}) }}
        >
          Buscar
        </Link>
        <Link
          to="/mis-libros"
          style={{
            ...styles.link,
            ...(location.pathname === '/mis-libros' ? styles.active : {}),
          }}
        >
          Mis Libros
        </Link>
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem 2rem',
    backgroundColor: '#fff',
    borderBottom: '1px solid #ccc',
    marginBottom: '1rem',
  },
  logo: {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    textDecoration: 'none',
    color: '#333',
  },
  links: {
    display: 'flex',
    gap: '1rem',
  },
  link: {
    textDecoration: 'none',
    color: '#333',
    fontWeight: '500',
  },
  active: {
    color: '#0066cc',
    borderBottom: '2px solid #0066cc',
    paddingBottom: '2px',
  },
};

export default Navbar;
