import React, { useState } from 'react';
import axios from 'axios';

function GuardarLectura() {
  const [libro, setLibro] = useState(null);
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [lugarFin, setLugarFin] = useState('');
  const [puntaje, setPuntaje] = useState('');
  const [comentario, setComentario] = useState('');

  const imagenFallback = "https://upload.wikimedia.org/wikipedia/commons/thumb/a/ac/No_image_available.svg/480px-No_image_available.svg.png";

  const buscarLibro = async (query) => {
    try {
      const res = await axios.get(`http://127.0.0.1:8000/api/buscar-libros/${query}/`);
      if (res.data.length > 0) setLibro(res.data[0]); // Usamos el primero como ejemplo
    } catch (err) {
      console.error('Error al buscar libro', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      usuario_id: 1, // hardcoded
      isbn: libro?.isbn || '',
      titulo: libro?.titulo || '',
      autor: libro?.autor || '',
      imagen_url: libro?.portada || '',
      fecha_inicio: fechaInicio,
      fecha_fin: fechaFin,
      lugar_fin: lugarFin,
      puntaje: puntaje || null,
      comentario,
    };

    try {
      await axios.post('http://127.0.0.1:8000/api/guardar-lectura/', payload, {
        headers: { 'Content-Type': 'application/json' }
      });
      alert("‚úÖ Lectura registrada");
    } catch (err) {
      console.error('‚ùå Error al guardar la lectura', err);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Guardar libro y registrar lectura</h2>

      {/* Buscar libro */}
      <div className="input-group mb-3">
        <input type="text" className="form-control" placeholder="Buscar t√≠tulo"
          onKeyDown={(e) => e.key === 'Enter' && buscarLibro(e.target.value)} />
        <button className="btn btn-primary" onClick={() => {
          const input = document.querySelector('input[type="text"]');
          buscarLibro(input.value);
        }}>Buscar</button>
      </div>

      {libro && (
        <div className="card mb-3">
          <div className="row g-0">
            <div className="col-md-2">
              <img src={libro.portada || imagenFallback} className="img-fluid rounded-start" alt="Portada" />
            </div>
            <div className="col-md-10">
              <div className="card-body">
                <h5 className="card-title">{libro.titulo}</h5>
                <p className="card-text">{libro.autor}</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Formulario de lectura */}
      {libro && (
        <form onSubmit={handleSubmit}>
          <div className="mb-2">
            <label>üìÖ Fecha de inicio</label>
            <input type="date" className="form-control" value={fechaInicio} onChange={(e) => setFechaInicio(e.target.value)} />
          </div>
          <div className="mb-2">
            <label>üìÖ Fecha de fin</label>
            <input type="date" className="form-control" value={fechaFin} onChange={(e) => setFechaFin(e.target.value)} />
          </div>
          <div className="mb-2">
            <label>üìç Lugar donde lo terminaste</label>
            <input type="text" className="form-control" value={lugarFin} onChange={(e) => setLugarFin(e.target.value)} />
          </div>
          <div className="mb-2">
            <label>‚≠ê Puntaje (1-10)</label>
            <input type="number" className="form-control" value={puntaje} onChange={(e) => setPuntaje(e.target.value)} min="1" max="10" />
          </div>
          <div className="mb-2">
            <label>üí¨ Comentario</label>
            <textarea className="form-control" value={comentario} onChange={(e) => setComentario(e.target.value)} rows="3" />
          </div>
          <button type="submit" className="btn btn-success">üìò Guardar lectura</button>
        </form>
      )}
    </div>
  );
}

export default GuardarLectura;
