// ✅ src/pages/VerLecturaPage.jsx
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const imagenFallback = "https://upload.wikimedia.org/wikipedia/commons/thumb/a/ac/No_image_available.svg/480px-No_image_available.svg.png";

const VerLecturaPage = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [lectura, setLectura] = useState(null);

  useEffect(() => {
    // Obtener la lectura desde localStorage (se guarda al hacer clic en 'Ver')
    const lecturaGuardada = localStorage.getItem('lectura_seleccionada');
    if (lecturaGuardada) {
      setLectura(JSON.parse(lecturaGuardada));
    }
  }, []);

  if (!lectura) {
    return <p className="text-danger">⚠️ No se encontró la lectura seleccionada.</p>;
  }

  return (
    <div className="container py-4">
      <button onClick={() => navigate(-1)} className="btn btn-secondary mb-4">⬅️ Volver</button>

      <div className="card p-4 shadow">
        <div className="text-center mb-4">
          <img
            src={lectura.imagen_url || imagenFallback}
            alt="Portada"
            style={{ width: '150px', height: '220px', objectFit: 'cover' }}
            onError={(e) => { e.target.src = imagenFallback; }}
          />
        </div>

        <h2 className="text-center">{lectura.titulo}</h2>
        <p><strong>Autor:</strong> {lectura.autor}</p>
        <p><strong>Usuario:</strong> {lectura.usuario}</p>
        <p><strong>Fecha de inicio:</strong> {lectura.fecha_inicio || 'N/A'}</p>
        <p><strong>Fecha de fin:</strong> {lectura.fecha_fin || 'N/A'}</p>
        <p><strong>Lugar donde lo terminó:</strong> {lectura.lugar_fin || 'N/A'}</p>
        <p><strong>Puntaje:</strong> {lectura.puntaje || 'N/A'}</p>
        <p><strong>Comentario:</strong> {lectura.comentario || 'Sin comentarios'}</p>
      </div>
    </div>
  );
};

export default VerLecturaPage;
