import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import RegistrarLectura from './RegistrarLectura';

function RegistrarLibro() {
  const { id } = useParams(); // id del libro
  const [libro, setLibro] = useState(null);
  const [cargando, setCargando] = useState(true);

  useEffect(() => {
    axios.get(`http://127.0.0.1:8000/api/libro/${id}/`)
      .then((res) => {
        setLibro(res.data);
        setCargando(false);
      })
      .catch((err) => {
        console.error('Error al cargar el libro', err);
        setCargando(false);
      });
  }, [id]);

  if (cargando) return <p>Cargando libro...</p>;

  if (!libro) return <p>Libro no encontrado</p>;

  return (
    <div className="container mt-4">
      <h2>Registrar Lectura</h2>
      <div className="d-flex mb-3 align-items-center">
        <img src={libro.imagen_url} alt={libro.titulo} height="150" className="me-3" />
        <div>
          <h4>{libro.titulo}</h4>
          <p>{libro.autor}</p>
        </div>
      </div>

      {/* Formulario */}
      <RegistrarLectura libroId={libro.id} onLecturaGuardada={() => alert('Lectura guardada ðŸŽ‰')} />
    </div>
  );
}

export default RegistrarLibro;
