import React, { useState } from 'react';
import axios from 'axios';

function BuscarLibro() {
  const [titulo, setTitulo] = useState('');
  const [resultados, setResultados] = useState([]);

  const imagenFallback = "https://upload.wikimedia.org/wikipedia/commons/thumb/a/ac/No_image_available.svg/480px-No_image_available.svg.png";

  const buscar = async () => {
    try {
      const res = await axios.get(`http://127.0.0.1:8000/api/buscar-libros/${titulo}/`);
      setResultados(res.data);
      console.log("Resultados recibidos:", res.data);
    } catch (err) {
      console.error('Error al buscar libros', err);
    }
  };

  const guardarLibro = async (libro) => {
    const payload = {
      isbn: libro.isbn || '',
      titulo: libro.titulo || '',
      autor: libro.autor || '',
      anio: libro.anio || null,
      portada: libro.portada || '',
      id_usuario: 1,
    };

    console.log("üì§ Enviando libro al backend:", payload);

    try {
      await axios.post('http://127.0.0.1:8000/api/guardar-libro/', payload, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      alert('üìò Libro guardado correctamente');
    } catch (err) {
      console.error('‚ùå Error al guardar el libro', err);
    }
  };

  const seleccionarLibro = (libro) => {
    localStorage.setItem("libro_seleccionado", JSON.stringify(libro));
    window.location.href = "/guardar-libro";
  };

  return (
    <div className="container mt-4">
      <h2>Buscar libro</h2>

      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
          placeholder="T√≠tulo del libro"
        />
        <button className="btn btn-primary" onClick={buscar}>
          Buscar
        </button>
      </div>

      {resultados.length === 0 && (
        <p className="text-muted">üîç No hay resultados para mostrar</p>
      )}

      {resultados.map((libro, index) => {
        return (
          <div key={index} className="card mb-3">
            <div className="row g-0">
              <div className="col-md-2 d-flex align-items-center justify-content-center p-2">
                <img
                  src={libro.portada || imagenFallback}
                  alt="Portada"
                  onError={(e) => { e.target.src = imagenFallback; }}
                  style={{ width: '100px', height: '150px', objectFit: 'cover' }}
                />
              </div>

              <div className="col-md-10">
                <div className="card-body">
                  <h5 className="card-title">{libro.titulo}</h5>
                  <p className="card-text">{libro.autor} ({libro.anio})</p>
                  <button className="btn btn-success me-2" onClick={() => guardarLibro(libro)}>
                    Guardar
                  </button>
                  <button className="btn btn-primary" onClick={() => seleccionarLibro(libro)}>
                    Seleccionar
                  </button>
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

export default BuscarLibro;
